import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      logoDropdownOpen: false, // State to track dropdown visibility for logo
    };
  }

  toggleLogoDropdown = () => {
    this.setState(prevState => ({
      logoDropdownOpen: !prevState.logoDropdownOpen, // Toggle dropdown visibility
    }));
  };

  render() {
    return (
      <div className='p-4 pb-5 flex bg-yellow-400'>
        {/* Logo with click event to toggle dropdown */}
        <div className="relative flex items-center">
          <div
            className={`hamburger-icon space-y-2 cursor-pointer z-50 ${this.state.logoDropdownOpen ? 'open' : ''}`}
            onClick={this.toggleLogoDropdown}
          >
            {/* Hamburger bars */}
            <div className={`block w-7 h-1 bg-black transition-all duration-300 ${this.state.logoDropdownOpen ? 'rotate-45 absolute' : ''}`}></div>
            <div className={`block w-7 h-1 bg-black transition-all duration-300 ${this.state.logoDropdownOpen ? 'opacity-0' : ''}`}></div>
            <div className={`block w-7 h-1 bg-black transition-all duration-300 ${this.state.logoDropdownOpen ? '-rotate-45 absolute' : ''}`}></div>
          </div>

          {/* Dropdown Menu under the logo */}
          <div className={`absolute left-0 mt-10 p-2 bg-yellow-400 text-black rounded-md shadow-lg transition-all ease-in-out duration-500 ${this.state.logoDropdownOpen ? 'max-h-[500px] opacity-100' : 'max-h-0 opacity-0 overflow-hidden'}`}>
            <ul className="space-y-2 mt-5">
              <li><Link to="/tech" className="block px-4">Tech</Link></li>
              <li><Link to="/sports" className="block px-4">Sports</Link></li>
              <li><Link to="health" className="block px-4">Health</Link></li>
              <li><Link to="/science" className="block px-4">Science</Link></li>
            </ul>
          </div>
        </div>

        {/* NewsWave Title */}
        <h1 className='text-3xl font-semibold text-black ml-3 lg:text-4xl'>NewsWave</h1>

        {/* Navigation items */}
        <div className='ml-5 flex space-x-3 mt-3 font-bold text-black lg:ml-[900px] lg:space-x-20 lg:text-2xl'>
          <Link to='/'>
            <h1>Home</h1>
          </Link>
          <Link to='/About'>
            <h1>About</h1>
          </Link>
          <Link to='/Contact'>
            <h1>Contact</h1>
          </Link>
        </div>
      </div>
    );
  }
}
