import React from "react";

export default function Home() {
  return (
    <>
    <div
      style={{
        height: "80vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "black", // Solid dark background
        color: "#fff",
      }}
    >
      <div
        style={{
          textAlign: "center",
          padding: "2rem",
          borderRadius: "12px",
        }}
      >
        {/* Tagline */}
        <h1
          style={{
            fontSize: "2.5rem",
            fontWeight: "bold",
            background: `linear-gradient(
              90deg,
              rgba(15, 209, 139, 1) 0%,
              rgba(48, 219, 201, 1) 63%,
              rgba(69, 249, 252, 1) 100%
            )`,
            WebkitBackgroundClip: "text",
            color: "transparent",
            marginBottom: "1rem",
          }}
        >
          Welcome to CloudNotes
        </h1>

        {/* Subtitle */}
        <p style={{ fontSize: "1rem", marginBottom: "1.5rem" }}>
          Write Anywhere, Access Everywhere. Organize your life effortlessly with CloudNotes.
        </p>

        {/* Get Started Button */}
        <button
          style={{
            padding: "0.8rem 2rem",
            fontSize: "1rem",
            color: "#fff",
            background: `linear-gradient(
              90deg,
              rgba(15, 209, 139, 1) 0%,
              rgba(48, 219, 201, 1) 63%,
              rgba(69, 249, 252, 1) 100%
            )`,
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            transition: "transform 0.2s ease",
          }}
          onMouseEnter={(e) => (e.target.style.transform = "scale(1.05)")}
          onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
        >
          Get Started
        </button>
      </div>
    </div>
    
    <div className="flex justify-center items-center h-96 bg-white">
    <div className="relative m-2 w-[190px] h-[254px] bg-black flex flex-col justify-end p-3 gap-3 rounded-lg cursor-pointer group">
      {/* Outer Gradient */}
      <div className="absolute inset-0 -left-[5px] w-[200px] h-[264px] rounded-lg bg-gradient-to-br from-[#e81cff] to-[#40c9ff] z-[-10] transition-all duration-600 ease-[cubic-bezier(0.175,0.885,0.32,1.275)] group-hover:rotate-[-90deg] group-hover:scale-[1.34] group-hover:scale-y-[0.77]"></div>

      {/* Inner Glow Effect */}
      <div className="absolute inset-0 bg-gradient-to-br from-[#fc00ff] to-[#00dbde] scale-[0.95] blur-[20px] z-[-1] group-hover:blur-[30px]"></div>

      {/* Card Content */}
      <p className="text-3xl font-bold capitalize text-white">Text Modifier</p>
      <p className="text-sm text-white">Powered By</p>
      <p className="text-sm font-semibold text-[#e81cff]"></p>
    </div>CloudNotes
    <div className="relative w-[190px] h-[254px] bg-black flex flex-col justify-end p-3 gap-3 rounded-lg cursor-pointer group">
      {/* Outer Gradient */}
      <div className="absolute inset-0 -left-[5px] w-[200px] h-[264px] rounded-lg bg-gradient-to-br from-[#e81cff] to-[#40c9ff] z-[-10] transition-all duration-600 ease-[cubic-bezier(0.175,0.885,0.32,1.275)] group-hover:rotate-[-90deg] group-hover:scale-[1.34] group-hover:scale-y-[0.77]"></div>

      {/* Inner Glow Effect */}
      <div className="absolute inset-0 bg-gradient-to-br from-[#fc00ff] to-[#00dbde] scale-[0.95] blur-[20px] z-[-1] group-hover:blur-[30px]"></div>

      {/* Card Content */}
      <p className="text-3xl font-bold capitalize text-white">NewsWave</p>
      <p className="text-sm text-white">Powered By</p>
      <p className="text-sm font-semibold text-[#e81cff]">CloudNotes</p>
    </div>
    </div>
    </>
  );
}
