import React,{useContext,useState} from "react";
import noteContext from "../Context/notes/noteContext"
import NoteItem from "./NoteItem";
// import noteState from "../Context/notes/noteState";
export default function Notes() {
    const context = useContext(noteContext)
    const {addnotes}= context
    const[notes,setnotes] = useState({title:"",description:""})
    const handlesubmitbutton =()=>{
      addnotes(notes.title,notes.description)
      console.log(notes)
    }
    const onchange =(e)=>{
setnotes({...notes,[e.target.name]:e.target.value})
    }

  return (
    <>
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white shadow-lg rounded-lg p-5 space-y-4">
        <h2 className="text-xl font-bold text-gray-800">Create a New Note</h2>
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700">
           Title
          </label>
          <input
            type="text"
            id="title"
            name="title"
            placeholder="Enter note title"
            className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-teal-500 focus:border-teal-500"
          onChange={onchange}/>
        </div>
        <div>
          <label htmlFor="description" className="block text-sm font-medium text-gray-700">
            Description
          </label>
          <textarea
            id="description"
            name="description"
            placeholder="Enter note description"
            rows="5"
            className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-teal-500 focus:border-teal-500" onChange={onchange}
          ></textarea>
        </div>
        <div className="flex justify-end">
          <button
            className="bg-teal-500 text-white px-4 py-2 mr-24 rounded-md hover:bg-teal-600 transition"
          onClick={handlesubmitbutton}>
            Save Note
          </button>
        </div>
      </div>
    </div>
    <div className="max-w-4xl mx-auto p-6">
    <div className="bg-white shadow-lg rounded-lg p-5 space-y-4">
      <h2 className="text-xl font-bold text-gray-800">Your Notes</h2>
      <div>
        {Array.isArray(notes) && notes.length > 0 ? (
          notes.map((note) => (
            <NoteItem
              key={note._id} // Ensure unique key for React rendering
              title={note.title}
              description={note.description}
            />
          ))
        ) : (
          <p className="text-gray-600">No notes available.</p>
        )}
       

       {/* style={{color: "#63E6BE",}} */}
      </div>
    </div>
  </div>
  </>
  );
}
