import React, { useContext, useState } from "react";
import noteContext from "../Context/notes/noteContext";
import NoteItem from "./NoteItem";

export default function Notes() {
  const context = useContext(noteContext);
  const { notes, editnotes, getnotes } = context;

  const [editModalOpen, setEditModalOpen] = useState(false); // Modal state
  const [currentNote, setCurrentNote] = useState({ id: "", title: "", description: "" }); // Current note being edited

  // Handle input change
  const handleInputChange = (e) => {
    setCurrentNote({ ...currentNote, [e.target.name]: e.target.value });
  };

  // Open modal with note data
  const handleEditClick = (note) => {
    setCurrentNote({ id: note._id, title: note.title, description: note.description });
    setEditModalOpen(true);
  };

  // Handle update
  const handleUpdate = async () => {
    await editnotes(currentNote.id, currentNote.title, currentNote.description); // Call editnotes from context
    setEditModalOpen(false); // Close modal
    getnotes(); // Refresh notes
  };
  const handleDeleteClick = (id) => {
    deletenotes(id); // Call the delete function from the context
  };

  return (
    <>
      <div className="max-w-4xl mx-auto p-6">
        <h2 className="text-xl font-bold text-gray-800 mb-4">Your Notes</h2>
        <div>
          {notes.length > 0 ? (
            notes.map((note) => (
              <NoteItem key={note._id} note={note} onEditClick={handleEditClick} onDeleteClick={handleDeleteClick}/>
            ))
          ) : (
            <p className="text-gray-600">No notes available.</p>
          )}
        </div>
      </div>

      {editModalOpen && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-md shadow-lg w-96">
            <h3 className="text-lg font-bold mb-4">Edit Note</h3>
            <div className="mb-4">
              <label className="block text-sm font-medium mb-1">Title</label>
              <input
                type="text"
                name="title"
                value={currentNote.title}
                onChange={handleInputChange}
                className="w-full p-2 border rounded-md"
              />
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium mb-1">Description</label>
              <textarea
                name="description"
                value={currentNote.description}
                onChange={handleInputChange}
                className="w-full p-2 border rounded-md"
              />
            </div>
            <div className="flex justify-end">
              <button
                onClick={() => setEditModalOpen(false)}
                className="bg-gray-500 text-white px-4 py-2 mr-2 rounded-md hover:bg-gray-600"
              >
                Cancel
              </button>
              <button
                onClick={handleUpdate}
                className="bg-teal-500 text-white px-4 py-2 rounded-md hover:bg-teal-600"
              >
                Update
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
